import pandas as pd
import matplotlib.pyplot as plt
plt.style.use("./style/simple_bold.mplstyle") #select stylesheet
from scipy.stats import linregress
pd.options.mode.copy_on_write = True #enable copy-on-write


df = pd.read_parquet("vinylphenol transfer hydrogenation(data).parquet") #import the data
df = df.query("temperature_C == 75 & time_min <= 30")

for rxn in df.rxn_label.unique(): #loop over unique reaction labels
    fit_result = linregress(df.query("rxn_label==@rxn")["time_min"], df.query("rxn_label==@rxn")["ethylphenol_mM"]) #fit the data
    df.loc[df["rxn_label"] == rxn, "fit_slope"] = fit_result.slope #extract the fit parameters
    df.loc[df["rxn_label"] == rxn, "fit_intercept"] = fit_result.intercept #extract the fit parameters
df["mass_activity_mM/g-min"] = df.fit_slope/df.catalyst_mass #calculate mass activity

print(df.loc[df["formate_mM"]==500, ["rxn_label", "IPA_molefrac", "mass_activity_mM/g-min"]].head(100).to_string()) #print the file head for inspection
df.query("catalyst == 'Pd'").plot.scatter(x="IPA_molefrac", y="mass_activity_mM/g-min", c="formate_mM", cmap="Set1", s=200, edgecolor="k", linewidth=1.5) #plot the data
plt.xlabel("Isopropanol Mole Fraction") #add x-axis label
plt.ylabel("Mass Activity (mM/g min)") #add y-axis label
plt.title("Pd") #add title
plt.show()
